import React, { Component } from "react"
import axios from 'axios'
import { Card, Header, Form, Input, Icon, Button } from "semantic-ui-react"

let endPoint = "http://localhost:9000";

class ToDOList extends Component {
    constructor(props) {
        super(props);

        this.state = {
            task: "",
            items: []
        }
    }

    onSubmit

    getTask = () => {
        axios.get(endPoint, "/api/task")
        .then((res) =>{
            // res.data is not null update the value
            if(res.data){
                this.setState({
                    items: res.ite.map((item)=>{

                    })
                });
            }
        });
    }

    undoTask

    deleteTask

    updateTask

    componentDidMount() {
        this.getTask();
    }

    taskChangeHandler = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    render() {
        return (
            <div>
                <div className="row">
                    <Header className="header" color="yellow">To Do List App</Header>
                </div>
                <div className="row">
                    <Form onSubmit={this.onSubmit}>
                        <Input
                            type="text"
                            name="task"
                            value={this.state.task}
                            onChange={this.taskChangeHandler}
                            placeholder="create task"
                            fluid
                        />

                        {/* <Button value="submit" type="submit">Add Task</Button> */}
                    </Form>
                </div>

                <div className="row">
                    <Card.Group>{this.state.items}</Card.Group>
                </div>

            </div>
        );
    }
}

export default ToDOList
