import React, { Component } from "react"
import axios from 'axios'
import { Card, Header, Form, Input, Icon, Button } from "semantic-ui-react"

let endPoint = "http://localhost:9000";

class ToDOList extends Component {
    constructor(props) {
        super(props);

        this.state = {
            task: "",
            items: []
        }
    }

    onSubmit

    getTask = () => {
        axios.get(endPoint, "/api/task")
            .then((res) => {
                // res.data is not null update the value
                if (res.data) {
                    this.setState({
                        items: res.data.map((item) => {
                            let color = "yellow";
                            let style = {
                                wordWrap: "break-word",
                            };

                            if (item.status) {
                                color = "green";
                                style["textDecorationLine"] = "line-through";
                            }

                            return (
                                <Card key={item._id} color={color} className="rough">
                                    <Card.Content>
                                        <Card.Header textAlign="left">
                                            <div style={style}>{item.task}</div>
                                        </Card.Header>

                                        <Card.Meta textAlign="right">
                                            <Icon name="check circle" color="blue"
                                                onClick={() => this.updateTask(item._id)}
                                            />
                                            <span style={{ paddingRight: 10 }}>Undo</span>
                                            <Icon
                                                name="delete"
                                                color="red"
                                                onClick={() => this.deleteTask(item._id)}
                                            />
                                            <span style={{ paddingRight: 10 }}>Delete</span>
                                        </Card.Meta>
                                    </Card.Content>
                                </Card>
                            );
                        }),
                    });
                }
                else{
                    this.setState({
                        items: [],
                    });
                }
            });
    };

unTask = (id) =>{
        axios.put(endPoint + "/api/task/" + id, {
            headers:{
                "Content-Type":"application/x-www-form-urlencoded",
            },
        }).then((res)=>{
            console.log(res);
            this.getTask();
        })
    }

    deleteTask

    updateTask = (id) =>{
        axios.put(endPoint + "/api/task/" + id, {
            headers:{
                "Content-Type":"application/x-www-form-urlencoded",
            },
        }).then((res)=>{
            console.log(res);
            this.getTask();
        })
    }

    componentDidMount() {
        this.getTask();
    }

    taskChangeHandler = (event) => {
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    render() {
        return (
            <div>
                <div className="row">
                    <Header className="header" color="yellow">To Do List App</Header>
                </div>
                <div className="row">
                    <Form onSubmit={this.onSubmit}>
                        <Input
                            type="text"
                            name="task"
                            value={this.state.task}
                            onChange={this.taskChangeHandler}
                            placeholder="create task"
                            fluid
                        />

                        {/* <Button value="submit" type="submit">Add Task</Button> */}
                    </Form>
                </div>

                <div className="row">
                    <Card.Group>{this.state.items}</Card.Group>
                </div>

            </div>
        );
    }
}

export default ToDOList
